set -ex

apt-get update
apt-get install -y --no-install-recommends ca-certificates
# For interacting with the Go source & subrepos:
apt-get install -y --no-install-recommends git-core openssh-client

apt-get clean
rm -fr /var/lib/apt/lists

mkdir -p ~/.ssh/
chmod 0700 ~/.ssh/

# Add Github.com's known_hosts entries, so git push calls later don't
# prompt, and don't need to have their strict host key checking
# disabled.
echo "|1|SFEvEAqYsJ18JCr+0iV4GtlwS4w=|P6oCZUUd/5t9pH4Om7ShlfltRyE= ssh-rsa " > ~/.ssh/known_hosts
echo "|1|HygGkfOGLovavKfixjXWFJ7Yk1I=|lb/724row8KDTMC1dZiJlHyjxWM= ssh-rsa " >> ~/.ssh/known_hosts
chmod 0600 ~/.ssh/known_hosts
